} actor CatDownloader { var decoder: JSONDecoder { let decoder = JSONDecoder() decoder.keyDecodingStrategy = .convertFromSnakeCase return decoder } // –í—ã –º–æ–∂–µ—Ç–µ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞—Ç—å —Å–æ–±—Å—Ç–≤–µ–Ω–Ω—ã–π URL –¥–ª—è –∞–≤–∞—Ç–∞—Ä–∫–∏ let avatarURL: URL = .init(string: "https://image.lexica.art/full_webp/4f696c7b-f280-43ce-80f0-dd211c7f553")! let catAPI: URL = .init(string: "https://raw.githubusercontent.com/Proekt-SwiftUI/sc-book/refs/heads/main/practice_data/cat_api.json?token=GHSAT0AAAAAACY4C77IXVFZ522PDU33FK6AZYPWQEQ")! // –°–∫–∞—á–∏–≤–∞–µ–º –∞–≤–∞—Ç–∞—Ä–∫—É –∫–æ—Ç–∞. –í —Å–ª—É—á–∞–µ –Ω–µ—É–¥–∞—á–∏ ‚Äî –≤–æ–∑–≤—Ä–∞—â–∞–µ–º nil // –î–ª—è –ø—Ä–æ—Å—Ç–æ—Ç—ã –ø—Ä–∏–º–µ—Ä–∞ —è –Ω–µ —Å—Ç–∞–ª –¥–µ–ª–∞—Ç—å –¥–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω—ã–µ –ø—Ä–æ–≤–µ—Ä–∫–∏, —Å—Ç–∞—Ç—É—Å –∫–æ–¥ –∏ –ø—Ä–æ—á–µ–µ. func donwloadCatAvatar() async throws -> UIImage? { let imageData: Data = try await URLSession.shared.data(for: .init(url: avatarURL)).0 guard let catImage: UIImage = UIImage(data: imageData) else { print("–ö–∞—Ä—Ç–∏–Ω–∫–∞ –∫–æ—Ü—ã–∫–∞ –Ω–µ –Ω–∞–π–¥–µ–Ω–∞ –∏/–∏–ª–∏ –Ω–µ –¥–æ—Å—Ç—É–ø–Ω–∞") return nil } return catImage } // –°–∫–∞—á–∏–≤–∞–µ–º —Å–ø–∏—Å–æ–∫ –∫–æ—Ç–æ–≤, –¥–µ–∫–æ–¥–∏—Ä—É–µ–º –≤ –º–∞—Å—Å–∏–≤ [Cat] –∏ –≤–æ–∑–≤—Ä–∞—â–∞–µ–º —Ä–µ–∑—É–ª—å—Ç–∞—Ç func getListOfCats() async throws -> [Cat] { let catsData: Data = try await URLSession.shared.data(for: .init(url: catAPI)).0 let cats: [Cat] = try decoder.decode([Cat].self, from: catsData) return cats }
